sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;

	function approvalTabController($scope, $location) {
	$scope.openProfile = function(user) {
		$location.search("id=user_profile&sys_id=" + user.sys_id);
	};
}
};

